<?php    require_once '../php/config.php';  require_once '../system/function.valpost.php';    header('Content-Type: application/json');    try {    if (!valpost($_POST, array('id'))) {      throw new Exception('Parámetros incorréctos.', 1);    }        require_once '../system/class.Session.php';        $session = Session :: get();        if ($session == null || !$session->admin) {      throw new Exception('No estás autorizado para ésto.', 2);    }        require_once '../php/connection.php';        if (!$mysqli) {      throw new Exception('Base de datos no disponible.', 3);    }        require_once '../system/class.Homework.php';        Homework :: $mysqli = $mysqli;    $homework = new Homework();    $homework->setId($_POST['id']);    $homework->correct();        echo json_encode(array(      'errno' => 0,       'message' => 'Tarea marcada como corregida.'    ));  } catch (Exception $ex1) {    echo json_encode(array(      'errno' => 1,       'message' => utf8_encode($ex1->getMessage())    ));  }