<?php    /**   * Copyright 2014 - Edgar Alexander Franco   *   * @author Edgar Alexander Franco   * @version 1.0.0   */    /**   * Encodes to UTF-8 the strings contained in the specified array.   *   * @param {array} $array Array to encode.   * @return {array}   */  function utf8encodearray ($array) {    foreach ($array as $key => $val) {      $type = gettype($val);            switch ($type) {        case 'array' : $array[ $key ] = utf8encodearray($val); break;        case 'string' : $array[ $key ] = utf8_encode($val); break;      }    }        return $array;  }