drop procedure if exists homework_get; delimiter // create procedure homework_get (in p_from bigint(18) unsigned, in p_to bigint(18) unsigned, in p_corrected bit(1))   begin     select homeworks.id as id, cedula, name, last_name, send_date from homeworks inner join users on homeworks.user_id = users.id where corrected = p_corrected order by homeworks.id desc limit p_from, p_to;   end   //   delimiter ; 